import DetailNavBar from "@/views/detail/childComps/DetailNavBar";
import { getDetail, Goods, Shop, GoodsParam, getRecommend } from "@/network/detail";
import DetailSwiper from "@/views/detail/childComps/DetailSwiper";
import DetailBaseInfo from "@/views/detail/childComps/DetailBaseInfo";
import DetailShopInfo1 from "@/views/detail/childComps/DetailShopInfo1";
import Scroll from "@/components/common/scroll/Scroll";
import DetailGoodsInfo from "@/views/detail/childComps/DetailGoodsInfo";
import DetailParamInfo from "@/views/detail/childComps/DetailParamInfo";
import DetailComment from "@/views/detail/childComps/DetailComment";
import DetailBottomBar from "@/views/detail/childComps/DetailBottomBar";
import Toast from "@/components/common/toast/Toast";
import GoodsListItem from "@/components/content/goods/GoodsListItem";
import GoodsList from "@/components/content/goods/GoodsList";
import { debounce } from "@/common/utils";
import { backTopMixin } from "@/common/mixin";
export default {
  name: "Detail",
  components: {
    DetailNavBar: DetailNavBar,
    DetailSwiper: DetailSwiper,
    DetailBaseInfo: DetailBaseInfo,
    DetailShopInfo1: DetailShopInfo1,
    Scroll: Scroll,
    Toast: Toast,
    DetailGoodsInfo: DetailGoodsInfo,
    DetailParamInfo: DetailParamInfo,
    DetailComment: DetailComment,
    GoodsListItem: GoodsListItem,
    GoodsList: GoodsList,
    DetailBottomBar: DetailBottomBar
  },
  mixins: [backTopMixin],
  data: function data() {
    return {
      iid: null,
      topimages: [],
      goods: {},
      shop: {},
      detailinfo: {},
      paramInfo: {},
      commentInfo: {},
      recommends: [],
      titleTopY: [],
      getThemeTopy: null,
      currentIndex: 0,
      show: false,
      message: ''
    };
  },
  created: function created() {
    var _this = this;

    this.iid = this.$route.params.iid;
    getDetail(this.iid).then(function (res) {
      //1.获取顶部轮播数据
      console.log(res);
      var data = res.result;
      _this.topimages = data.itemInfo.topImages; //2.获取商品信息

      _this.goods = new Goods(data.itemInfo, data.columns, data.shopInfo.services); //3.获取店铺信息

      _this.shop = new Shop(data.shopInfo); //4.保存商品的详情数据

      _this.detailinfo = data.detailInfo; //5.获取参数信息

      _this.paramInfo = new GoodsParam(data.itemParams.info, data.itemParams.rule); //6.获取评论信息

      _this.commentInfo = data.rate.list[0]; //7.获取推荐信息
    });
    getRecommend().then(function (res) {
      _this.recommends = res.data.list;
    });
    this.getThemeTopy = debounce(function () {
      _this.$nextTick(function () {
        //获取不同组件的offsetTop
        _this.titleTopY = [];

        _this.titleTopY.push(0);

        _this.titleTopY.push(_this.$refs.params.$el.offsetTop);

        _this.titleTopY.push(_this.$refs.comment.$el.offsetTop);

        _this.titleTopY.push(_this.$refs.recommends.$el.offsetTop);

        _this.titleTopY.push(Number.MAX_VALUE);

        console.log(_this.titleTopY);
      });
    }, 100);
  },
  updated: function updated() {},
  mounted: function mounted() {
    var newRefresh = debounce(this.$refs.scroll.refresh, 100);
    this.$bus.$on('detailImageLoad', function () {
      newRefresh();
    });
  },
  methods: {
    imageload: function imageload() {
      this.$refs.scroll.refresh(); //图片加载获取offsettop

      this.getThemeTopy();
    },
    //点击导航栏跳转页面
    titleClick: function titleClick(index) {
      this.$refs.scroll.scrollTo(0, -this.titleTopY[index], 100);
    },
    //滚动详情页与顶部导航栏的对应
    contentScroll: function contentScroll(position) {
      var positionY = -position.y;
      var length = this.titleTopY.length; // for (let i in this.titleTopY){
      //   if (positionY > this.titleTopY[i] && positionY < this.titleTopY[i+1]){
      //     console.log(i)
      //   }
      // }

      for (var i = 0; i < this.titleTopY.length - 1; i++) {
        if (this.currentIndex !== i && positionY >= this.titleTopY[i] && positionY < this.titleTopY[i + 1]) {
          this.currentIndex = i;
          console.log(this.currentIndex);
          this.$refs.nav.currentIndex = this.currentIndex;
        }
      } //抽取混入


      this.listenShowBackTop(position);
    },
    //加入购物车
    addToCart: function addToCart() {
      var _this2 = this;

      //获取数据
      var product = {};
      product.image = this.topimages[0];
      product.title = this.goods.title;
      product.desc = this.goods.desc;
      product.price = this.goods.oldPrice;
      product.iid = this.iid; //添加到购物车
      // this.$store.commit('addCart',product)

      this.$store.dispatch('addCart', product).then(function (res) {
        _this2.show = true;
        _this2.message = res;
        setTimeout(function () {
          _this2.show = false;
          _this2.message = '';
        }, 1500); // console.log(this.$toast)
        // this.$toast.Show()
      });
    }
  }
};
